sqlMap.procedure:
BEGIN
	GIUW_POL_DIST_PKG.delete_dist_master_tables(?);

	UPDATE giuw_pol_dist
	   SET auto_dist = 'N'
	 WHERE dist_no = ? ;
END;

    	BEGIN
        	UPDATE giuw_pol_dist
			   SET auto_dist = #autoDist#
			 WHERE dist_no = #distNo# ;
        END;

    	BEGIN
        	UPDATE giuw_pol_dist
			   SET special_dist_sw = 'N'
			 WHERE dist_no = #distNo# ;
        END;

sqlMap.delete:
    	BEGIN
    		giis_users_pkg.app_user := #userId#;
			DELETE FROM GIUW_WITEMDS
         	 WHERE dist_no = #distNo#;
			DELETE FROM GIUW_WPOLICYDS
         	 WHERE dist_no = #distNo#;
    	END;

sqlMap.procedure:
	BEGIN
	  UPDATE giuw_pol_dist
         SET dist_flag = #distFlag#
       WHERE dist_no = #distNo# ;
	END;
